// Parse the AI response to extract structured data
const aiResponse = JSON.parse($input.first().json.message.content);
const telegramData = $('Telegram Trigger').first().json;

// Store the original message data
const result = {
  chatId: telegramData.message.chat.id,
  messageId: telegramData.message.message_id,
  userId: telegramData.message.from.id,
  username: telegramData.message.from.username,
  originalText: $('Speech to Text').first().json.text,
  intent: aiResponse.intent,
  confidence: aiResponse.confidence,
  extractedData: aiResponse.extractedData,
  needsConfirmation: aiResponse.needsConfirmation,
  aiResponse: aiResponse.response
};

return result;

// Format the MCP response for Telegram
const mcpResult = $input.first().json;
const processedData = $('Process Data').first().json;

let responseText = '';

try {
  const result = JSON.parse(mcpResult.content[0].text);

  switch(processedData.intent) {
    case 'create_client':
      responseText = `‚úÖ Client created successfully!\n\nName: ${result.name}\nEmail: ${result.email || 'Not provided'}\nPhone: ${result.phone || 'Not provided'}\nID: ${result.id}`;
      break;

    case 'create_job':
      responseText = `‚úÖ Job scheduled successfully!\n\nTitle: ${result.title}\nClient: ${result.clientName || 'Client ID: ' + result.clientId}\nScheduled: ${result.scheduledStart ? new Date(result.scheduledStart).toLocaleString() : 'Not scheduled'}\nStatus: ${result.status}\nJob ID: ${result.id}`;
      break;

    case 'create_invoice':
      responseText = `‚úÖ Invoice created successfully!\n\nInvoice #: ${result.invoiceNumber}\nClient: ${result.clientName || 'Client ID: ' + result.clientId}\nTotal: $${result.total}\nStatus: ${result.status}\nInvoice ID: ${result.id}`;
      break;

    case 'create_estimate':
      responseText = `‚úÖ Estimate created successfully!\n\nEstimate #: ${result.estimateNumber}\nClient: ${result.clientName || 'Client ID: ' + result.clientId}\nTotal: $${result.total}\nValid Until: ${result.validUntil ? new Date(result.validUntil).toLocaleDateString() : 'No expiration'}\nEstimate ID: ${result.id}`;
      break;

    case 'get_jobs':
      if (Array.isArray(result) && result.length > 0) {
        responseText = `üìÖ Found ${result.length} job(s):\n\n`;
        result.slice(0, 5).forEach((job, index) => {
          responseText += `${index + 1}. ${job.title}\n`;
          responseText += `   Client: ${job.clientName || 'Client ID: ' + job.clientId}\n`;
          responseText += `   Status: ${job.status}\n`;
          responseText += `   Scheduled: ${job.scheduledStart ? new Date(job.scheduledStart).toLocaleString() : 'Not scheduled'}\n\n`;
        });
        if (result.length > 5) {
          responseText += `...and ${result.length - 5} more jobs.`;
        }
      } else {
        responseText = 'üìÖ No jobs found for the specified criteria.';
      }
      break;

    case 'get_clients':
      if (Array.isArray(result) && result.length > 0) {
        responseText = `üë• Found ${result.length} client(s):\n\n`;
        result.slice(0, 5).forEach((client, index) => {
          responseText += `${index + 1}. ${client.name}\n`;
          responseText += `   Email: ${client.email || 'Not provided'}\n`;
          responseText += `   Phone: ${client.phone || 'Not provided'}\n\n`;
        });
        if (result.length > 5) {
          responseText += `...and ${result.length - 5} more clients.`;
        }
      } else {
        responseText = 'üë• No clients found.';
      }
      break;

    case 'update_job_status':
      responseText = `‚úÖ Job status updated!\n\nJob: ${result.title}\nNew Status: ${result.status}\nUpdated: ${new Date().toLocaleString()}`;
      break;

    case 'get_revenue':
      responseText = `üí∞ Revenue Statistics\n\nTotal Revenue: $${result.total || 0}\nNumber of Invoices: ${result.count || 0}\nPeriod: ${processedData.extractedData.month || new Date().getMonth() + 1}/${processedData.extractedData.year || new Date().getFullYear()}`;
      break;

    default:
      responseText = `‚úÖ Operation completed successfully!\n\nResult: ${JSON.stringify(result, null, 2)}`;
  }
} catch (error) {
  responseText = `‚ùå Error processing request: ${error.message}\n\nRaw response: ${JSON.stringify(mcpResult, null, 2)}`;
}

return {
  chatId: processedData.chatId,
  messageId: processedData.messageId,
  responseText: responseText
};
