// Check if message is a voice message, text, or command
const message = $input.first().json.message;
const chatId = message.chat.id;

let messageText = '';
let messageType = 'text';
let hasVoice = false;

// Handle voice messages
if (message.voice) {
  messageType = 'voice';
  hasVoice = true;
  messageText = '[Voice Message - needs transcription]';
} else if (message.audio) {
  messageType = 'audio';
  hasVoice = true;
  messageText = '[Audio Message - needs transcription]';
} else if (message.text) {
  messageText = message.text;
  messageType = 'text';
}

// Check if it's already a structured command
const isStructuredCommand = messageText.startsWith('/') && messageText.includes(':');

return {
  chatId: chatId,
  originalMessage: messageText,
  messageType: messageType,
  hasVoice: hasVoice,
  isStructuredCommand: isStructuredCommand,
  needsAiProcessing: !isStructuredCommand, // Only process with AI if it's not already structured
  voiceFileId: message.voice?.file_id || message.audio?.file_id || null,
  messageId: message.message_id
};

// Process AI response and create structured command
const aiResponse = $input.first().json.choices[0].message.content.trim();
const originalData = $node["Analyze Message Type"].json;

// If AI couldn't process or asked for clarification
if (!aiResponse.startsWith('/')) {
  return {
    ...originalData,
    processedCommand: null,
    needsUserResponse: true,
    aiResponse: aiResponse,
    command: 'clarification_needed'
  };
}

// Parse the structured command from AI
const commandMatch = aiResponse.match(/^\/([a-zA-Z_]+)/);
const command = commandMatch ? commandMatch[1] : 'help';

// Parse parameters using regex patterns
const parseParams = (text) => {
  const params = {};

  // Match key:"value" or key:value patterns
  const paramMatches = text.match(/([a-zA-Z]+):("[^"]*"|[^\s]+)/g) || [];

  paramMatches.forEach(match => {
    const [key, value] = match.split(':');
    params[key] = value.replace(/"/g, ''); // Remove quotes
  });

  return params;
};

const parameters = parseParams(aiResponse);

return {
  ...originalData,
  processedCommand: aiResponse,
  command: command,
  parameters: parameters,
  // Extract common parameters with defaults
  clientId: parameters.clientId || parameters.client || 1,
  title: parameters.title || `${command} from Natural Language`,
  description: parameters.description || `Created via natural language: "${originalData.originalMessage}"`,
  scheduledStart: parameters.scheduledStart || parameters.start || new Date().toISOString(),
  scheduledEnd: parameters.scheduledEnd || parameters.end,
  priority: parameters.priority || 'normal',
  status: parameters.status || 'draft',
  name: parameters.name,
  email: parameters.email,
  phone: parameters.phone,
  address: parameters.address,
  jobId: parameters.jobId || parameters.job,
  dueDate: parameters.dueDate || parameters.due,
  validUntil: parameters.validUntil || parameters.valid,
  invoiceId: parameters.invoiceId || parameters.invoice,
  estimateId: parameters.estimateId || parameters.estimate,
  items: parameters.items ? JSON.parse(parameters.items) : [],
  needsUserResponse: false
};

// For structured commands, parse them directly
